package com.class5;

public class Pyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for( int i=1;i<=5;i++) {
	for(int a=1;a<=i;a++) {
		System.out.print(a);
	}
	System.out.println();
}
System.out.println("----------");
for(int a=1;a<=5; a++) {
	for(int b=1;b<=a; b++) {
		System.out.print(2*b -1);
	}
	System.out.println();
}

}
	}


